function MinutesToTime(e) {
    e *= 60;
    const h = Math.floor(e / 3600).toString().padStart(2, '0'),
        m = Math.floor(e % 3600 / 60).toString().padStart(2, '0'),
        s = Math.floor(e % 60).toString().padStart(2, '0');

    return h + ':' + m + ':' + s;
    //return `${h}:${m}:${s}`;
}

var messages = [];
var mashSteps = global.get("mashSteps");

for (let i = 0; i < node.outputCount; i++ ) {
    if( i < mashSteps.length ) {
        messages.push({ 
            payload: MinutesToTime(mashSteps[i].stepTime),
            label: mashSteps[i].displayStepTemp + '\u00B0' + 'F'  
        });
    }
    else {
        messages.push({
            payload: '00:00:00',
            label: 'Undefined'
        });    
    }
}

return messages;

var count = 0;
var duration = 0;

if( msg.topic == "reset" ) {
    context.set("count", 0);
    context.set("duration", Date.now());
}
else {
    count = context.get("count");
    context.set("count", ++count);
    duration = Date.now() - context.get("duration");
    context.set("duration", duration);

}

msg.payload = count;
msg.duration = duration / 1000.0; // Seconds
return msg;

